import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <div className="min-h-screen bg-panel-bg py-12 px-4">
      <div className="container mx-auto max-w-3xl bg-white rounded-lg shadow-md p-8">
        <motion.h1 
          className="text-3xl font-bold text-dark-blue mb-6"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          Haqqımızda
        </motion.h1>
        <motion.p 
          className="text-lg text-gray-700 mb-4"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.5 }}
        >
          <b>AutiSense</b> autizmli uşaqlar üçün fərdiləşdirilmiş, AI dəstəkli öyrənmə platformasıdır. Məqsədimiz uşaqların gündəlik həyat bacarıqlarını əyləncəli və interaktiv şəkildə inkişaf etdirmək, valideyn və mütəxəssisləri bir araya gətirməkdir.
        </motion.p>
        <motion.ul 
          className="list-disc pl-6 text-gray-600 space-y-2"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.5 }}
        >
          <li>Uşaqlar üçün animasiyalı dərslər və oyunlar</li>
          <li>Valideynlər üçün AI analiz və fərdi tövsiyələr</li>
          <li>Mütəxəssislərlə əlaqə və dəstək imkanı</li>
        </motion.ul>
        <motion.p 
          className="mt-6 text-gray-500 text-sm"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.6, duration: 0.5 }}
        >
          Platformamız hələ prototip mərhələsindədir və real backend olmadan işləyir. Gələcəkdə daha çox funksiya əlavə olunacaq.
        </motion.p>
      </div>
    </div>
  );
};

export default About;
